sys_ux_client_script.required_translations:
[
    {
        "message": "Select a standalone issue from the list and make it as a parent to create a group"
    }
]

sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  if(api.state.groupTypeField.selectedItem == "make_standalone_issue_parent"){
    return helpers.translateSync("Select a standalone issue from the list and make it as a parent to create a group");
  }
	return '';
}
