sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
        var {
        name,
        payload
    } = event;
        var parentIssueFieldObj = api.state.parentIssueField;
    if (name === 'NOW_RECORD_LIST_CONNECTED#ROW_CLICKED') {

        if (event.elementId == 'list_1') {
            //if user selected an issue from the modal over modal
            parentIssueFieldObj.issueName = payload.row.displayValue.value;
            parentIssueFieldObj.issueId = payload.sys_id;
            api.setState('parentIssueField', parentIssueFieldObj);
            api.data.look_up_record.refresh();
            api.data.get_row_count_of_table.refresh();
        }
    }
}
