sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    helpers.snHttp('/api/sn_grc/issuegroupingapi/groupIssues', {
        method: "POST",
        body: {
            table: api.context.props.table,
            sysIds: api.context.props.sysIds,
            selected_group_type: api.state.groupTypeField.selectedItem,
            grouping_type: api.data.validate_issue_grouping.output.groupingType,
            new_parent_issue_name: api.state.newParentIssueNameField,
            parent_issue: api.state.parentIssueField.issueId,
            management_method: api.state.managementMethodField.selectedItem,
            description: api.state.descriptionField.data,
            deleteGroup: api.state.deleteGroups
        },
        batch: false

    }).then(({
        response
    }) => {
        if (response.result.status === 200) {
            var parentId = response.result.sysId;
            helpers.modal.close();
            helpers.navigate.to("record", {
                "table": "sn_grc_issue",
                "sysId": parentId
            });
        }
    }).catch(({
        error
    }) => {
        var errMessage = helpers.translateSync("Grouping failed due to some errors. Try again after some time or contact your administrator.");
        var messagesObj = api.state.messages;
        messagesObj.groupError.text = errMessage;
        messagesObj.groupError.dismissed = false;
        api.setState('messages',messagesObj);
        api.data.look_up_records.refresh();
    });
}
