sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var {
        name,
        payload
    } = event;
    if (name === 'NOW_ALERT#ACTION_CLICKED') {
        var messageObj = api.state.messages;
        if (event.elementId == 'group_type_info') {
            messageObj.groupType.dismissed = true;
        }
        if (event.elementId == 'mgmt_method_warning') {
            messageObj.mgmtMethod.dismissed = true;
        }
        if (event.elementId == 'group_error') {
            messageObj.groupError.dismissed = true;
        }
        api.setState('messages', messageObj);
        api.data.look_up_records.refresh();
    }
}
