sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    var children = [];
    var headerLabel = helpers.translateSync("Searching...");
    const output = api.data.look_up_records.results;
    if (output) {
        headerLabel = api.data.get_row_count_of_table.output.resultMessage;
        children = output.map(element => ({
            id: element._row_data.uniqueValue,
            label: element.short_description.value
        }));
    }

    var headerWithChild = {
        id: "searchResult",
        label: headerLabel,
        children: children
    };
    return [headerWithChild];

}
