sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    api.setState("selected_radio_option", event.payload.value);
    api.setState("selected_negotiation_event", null);
    api.setState("short_description", "");
    api.setState("negotiation_type", null);
    api.setState("negotiation_outcome", null);
    api.setState("expected_start_date", "");
}
