sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    if (api.state.assessemntTemplateId != '') {
        var asmtTemplateUpdated = true;
        var templates = api.data.get_assessment_templates_1.output;
        for (var tmp = 0; tmp < templates.length; tmp++) {
            var template = templates[tmp];
            if (template.id == api.state.assessemntTemplateId) {
                asmtTemplateUpdated = false;
            }
        }
        if (asmtTemplateUpdated) {
            api.setState('assessemntTemplateId', '');
        }

    }
}
