sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var {
        name,
        payload
    } = event;
    var descriptionFieldObj = api.state.descriptionField;
    if (name === 'NOW_TEXTAREA#VALUE_SET') {
        // searching the element
        if (event.elementId == 'description') {
            if (payload.value) {
                descriptionFieldObj.data = payload.value;
                api.setState('descriptionField', descriptionFieldObj);
            }
        }
    }
}
