sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  return api.state.selected_radio_option == 'existing_ne' ? api.state.selected_negotiation_event == null :
  (api.state.short_description == null || api.state.negotiation_outcome == null
  || api.state.negotiation_type == null || api.state.short_description == "");
}
