sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    if (api.context.props.table == 'sn_grc_profile') {
        helpers
            .translate('Initiated privacy assessment generation. Duplicate assessments will not be generated.')
            .then((val) => {
                api.setState('successMessage', val);
            });
    } else if (api.context.props.table == 'sn_grc_profile_type') {
        helpers
            .translate('Initiated assigning of privacy assessment to the Entity owners based on the selection. To see the generated assessments, navigate to "Privacy Assessments" related list. If assessments are not assigned to entity owners, it may be due to various reasons, such as duplicate assessments, unspecified entity owner values in records, inactive or retired status of the entity or associated processing activities, or insufficient permissions or roles for the entity owner to work on privacy assessments.')
            .then((val) => {
                api.setState('successMessage', val);
            });

    } else if (api.context.props.table == 'sn_privacy_processing_activity') {
        helpers
            .translate('Initiated privacy assessment generation for the entity. Duplicate assessments will not be generated.')
            .then((val) => {
                api.setState('successMessage', val);
            });
    }
}
