sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var {
        name,
        payload
    } = event;

    if (event.elementId == 'parent_issue') {

        var newValue = '';
        var managementMethodFieldObj = api.state.managementMethodField;
        var groupTypeFieldObj = api.state.groupTypeField;
        var descriptionFieldObj = api.state.descriptionField;
        var parentIssueFieldObj = api.state.parentIssueField;
        var issueFiltersObj = api.state.issueFilters;

        if (name === 'NOW_TYPEAHEAD#VALUE_SET') {
            // when parent issue field changes then adjust the filter and get the value of the description and management method
            if (payload.value) {
                newValue = payload.value;
            }
            parentIssueFieldObj.issueName = newValue;
            if (groupTypeFieldObj.selectedItem == 'add_to_existing_group'){
                issueFiltersObj.parentIssueSearchFilter = issueFiltersObj.parentIssueFilter;
            }
            if (groupTypeFieldObj.selectedItem == 'make_standalone_issue_parent'){
                issueFiltersObj.parentIssueSearchFilter = issueFiltersObj.standaloneIssueFilter;
            }
            issueFiltersObj.parentIssueSearchFilter = issueFiltersObj.parentIssueSearchFilter + '^short_descriptionSTARTSWITH' + newValue;
            api.setState('parentIssueField', parentIssueFieldObj);
            api.setState('issueFilters', issueFiltersObj);
            api.data.look_up_records.refresh();
            api.data.get_row_count_of_table.refresh();
        }

        if (name === 'NOW_TYPEAHEAD#SELECTED_ITEM_SET') {
            if (payload.value) {
                parentIssueFieldObj.issueId = payload.value;
                api.setState('parentIssueField', parentIssueFieldObj);
                api.data.look_up_record.refresh();
                api.data.get_row_count_of_table.refresh();
            }
        }
    }


}
