sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var {
        name,
        payload
    } = event;

    var managementMethodFieldObj = api.state.managementMethodField;
    if (name === 'NOW_SELECT#SELECTED_ITEM_SET') {

        if (event.elementId == 'management_method') {
            if (payload.value) {
                managementMethodFieldObj.selectedItem = payload.value;
                api.setState('managementMethodField', managementMethodFieldObj);
                var messageObj = api.state.messages;
                if (payload.value == '10') {
                    // Triggering a warning message when manage parent is selected
                    messageObj.mgmtMethod.dismissed = false;
                }
                else{
                    // Removing warning message when manage child is selected
                    messageObj.mgmtMethod.dismissed = true;
                }
                api.setState('messages', messageObj);
                api.data.look_up_records.refresh();
                api.data.get_row_count_of_table.refresh();

            }

        }
    }
}
