sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var {
        name,
        payload
    } = event;
    if (name === 'DATA_FETCH_SUCCEEDED') {
        if (event.elementId == 'validate_issue_grouping') {
            // if validate issue grouping succeed then setting the state parameters
            api.setState('groupTypeField', api.data.validate_issue_grouping.output.groupTypeField);
            api.setState('allowGrouping', api.data.validate_issue_grouping.output.allowGrouping);
            api.setState('managementMethodField', api.data.validate_issue_grouping.output.managementMethodField);
            api.setState('issueFilters', api.data.validate_issue_grouping.output.issueFilters);
            api.setState('issueColumns', api.data.validate_issue_grouping.output.issueColumns);
            api.setState('descriptionField',api.data.validate_issue_grouping.output.descriptionField);
            api.setState('parentIssueField', api.data.validate_issue_grouping.output.parentIssueField);
            api.setState('newParentIssueNameField', api.data.validate_issue_grouping.output.newParentIssueNameField);
            api.setState('messages', api.data.validate_issue_grouping.output.messages);
        }
        if (event.elementId == 'look_up_record') {
            // Looking up the description and management method of the selected issue
            var descriptionFieldObj = api.state.descriptionField;
            var managementMethodFieldObj = api.state.managementMethodField;
            var messageObj = api.state.messages;
            var descriptionField = api.data.look_up_record.result.description;
            var managementMethodField = api.data.look_up_record.result.management_method;
            if(descriptionField && descriptionField.value) {
                descriptionFieldObj.data = descriptionField.value;
            }
            else{
                descriptionFieldObj.data = "";
            }
            if(managementMethodField && managementMethodField.value){
                managementMethodFieldObj.selectedItem = managementMethodField.value;
            }
            else{
                managementMethodFieldObj.selectedItem = "";
                // remove warning message
                messageObj.mgmtMethod.dismissed = true;
                api.setState('messages', messageObj);
            }
            api.setState('descriptionField',descriptionFieldObj);
            api.setState('managementMethodField',managementMethodFieldObj);
        }
    }

}
