sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {

    // If you have selected a group type and management method
    var groupTypeFieldObj = api.state.groupTypeField;
    var managementMethodFieldObj = api.state.managementMethodField;
    var parentIssueFieldObj = api.state.parentIssueField;
    var newParentIssueNameField = api.state.newParentIssueNameField;

    if (groupTypeFieldObj.selectedItem != '' && managementMethodFieldObj.selectedItem != '') {
        // if selected group type is create new group and new parent issue name is given
        if (groupTypeFieldObj.selectedItem == 'create_new_group' && newParentIssueNameField != '') {
            return false;
        }
        // if selected group type is not create new group and you have selected an existing parent issue
        if (groupTypeFieldObj.selectedItem != 'create_new_group' && parentIssueFieldObj.issueId != '-1') {
            return false;
        }
    }
    return true;
}
